import { motion, AnimatePresence } from 'framer-motion';

const backdrop = {
	visible: { opacity: 1 },
	hidden: { opacity: 0 }
};
export const Modal = ({ showModal, setShowModal }) => {
	return (
		<AnimatePresence exitBeforeEnter>
			{showModal && (
				<motion.div className=" flex justify-center items-center fixed inset-0 h-full w-full bg-black opacity-25 ">
					<motion.div className="bg-white h-64 w-64 p-16 rounded-lg shadow-xl opacity-100">
						<p> Want to make another pizza?</p>
					</motion.div>
				</motion.div>
			)}
		</AnimatePresence>
	);
};
