import { motion, AnimatePresence } from 'framer-motion';

const backdrop = {
	visible: { opacity: 1 },
	hidden: { opacity: 0 }
};
export const Modal = ({ showModal, setShowModal }) => {
	return (
		<AnimatePresence exitBeforeEnter>
			{showModal && (
				<div className="fixed inset-0 h-full w-full z-10">
					<motion.div
						animate="visible"
						initial="hidden"
						variants={backdrop}
						className=" flex justify-center items-center  bg-black opacity-25 "
					/>
					<motion.div className="bg-white h-64 w-64 p-16 rounded-lg shadow-xl opacity-100">
						<p> Want to make another pizza?</p>
					</motion.div>
				</div>
			)}
		</AnimatePresence>
	);
};
