import { motion, AnimatePresence } from 'framer-motion';

const backdrop = {
	visible: { opacity: 0.5 },
	hidden: { opacity: 0 }
};
export const Modal = ({ showModal, setShowModal }) => {
	return (
		<AnimatePresence exitBeforeEnter>
			{showModal && (
				<motion.div
					variants={backdrop}
					initial="hidden"
					animate="visible"
					className="fixed inset-0 h-full w-full bg-black  "
				>
					<div className="bg-green-500" />
				</motion.div>
			)}
		</AnimatePresence>
	);
};
